mapper.select:
select
  id, task_id, user_id, operate_time, content, status, remark
from task_info_log


        and id = #{id}


        and task_id = #{taskId}


        and user_id = #{userId}


        and operate_time = #{operateTime}


        and content = #{content}


        and status = #{status}


        and remark = #{remark}
