mapper.select:
select

from task_info


    AND task_id = #{taskId}


    AND task_name like concat('%', #{taskName}, '%')


    AND task_tag = #{taskTag}


    AND users_group = #{usersGroup}


    AND start_time = #{startTime}


    AND stop_time = #{stopTime}


    AND status = #{status}


    AND create_by = #{createBy}


    AND create_time = #{createTime}


    AND update_by = #{updateBy}


    AND update_time = #{updateTime}


    AND remark = #{remark}


    AND project_id = #{projectId}


order by status asc

select

from task_info


    AND task_id != #{taskId}


    AND project_id = #{projectId}


    AND task_name = #{taskName}

mapper.insert:

insert into task_info (projectId, task_name, task_tag, 
  users_group, start_time, stop_time, 
  `status`, create_by, create_time, 
  update_by, update_time, remark
  )
values (#{projectid,jdbcType=BIGINT}, #{taskName,jdbcType=VARCHAR}, #{taskTag,jdbcType=SMALLINT}, 
  #{usersGroup,jdbcType=VARCHAR}, #{startTime,jdbcType=TIMESTAMP}, #{stopTime,jdbcType=TIMESTAMP}, 
  #{status,jdbcType=SMALLINT}, #{createBy,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{remark,jdbcType=VARCHAR}
  )
